'use strict';
const client = require('supertest');

describe('basic test', function () {
    it('basic not found', function (done) {
        const scaffold = require('../');
        const config = require('./config');
        const app = scaffold(config);
        client(app.listen())
            .get('/notfound')
            .expect(404, done);
    });
});